SQL_STMT_U.val:
SELECT INVO.INVO_DEBH_NO, INVO.INVO_INVOICENO, INVO.INVO_AINITIALH,INVO.INVO_INVDATE, INVO.INVO_DUEDATE, INVO.INVO_AACTUALH, INVO.INVO_FINALPAYMENTDATE from :1/INVO INVO LEFT OUTER JOIN :1/BPIV BPIV ON INVO.INVO_NO = BPIV.BPIV_INVO_NO where INVO.INVO_AINITIALH > 0 and INVO.INVO_SCHT_NO > 0 and (INVO.INVO_MARKCODESPEC = 0 or (INVO.INVO_MARKCODESPEC > 0 and exists(select * from :1/MARK MARK where MARK.MARK_CODE = INVO.INVO_MARKCODESPEC and MARK.MARK_SELECT_PAY_EXP = 'Y'))) and (INVO.INVO_FINALPAYMENTDATE is null or ((INVO.INVO_FINALPAYMENTDATE >= date(:3) and INVO.INVO_FINALPAYMENTDATE <= date(:4)) and BPIV.BPIV_BTCH_BATCHCODE in (':5', ':6', ':7'))) and INVO.INVO_DEBC_NO in ( SELECT DEBC.DEBC_NO from :1/DEBC DEBC, :1/DEBB DEBB, :1/ADMN ADMN, :1/CLNT CLNT, :1/CMON CMON where DEBC.DEBC_ADMNO = ADMN.ADMN_NO and DEBC.DEBC_ADMNO = CLNT.CLNT101_ADMNO and DEBC.DEBC_CLNTNO = CLNT.CLNT102_CLNTNO and ADMN.ADMN_DELCREDERE = 'N' and ADMN.ADMN_ENDCLIENTS = 'N' and ADMN.ADMN_TESTCLIENTS = 'N' and ADMN.ADMN_PAYMENTEXPTOGRAYDON = 'Y' and CLNT.CLNT212_CONFIDENTIAL = 'N' and CLNT.CLNT212_INTERCREDIT = 'N' and CLNT.CLNT906_STATUSCONTRACT = 1 and CLNT.CLNT804_TYPEOFINVESTMENT in (1,11) and CLNT.CLNT301_DUNNING = 'Y' and DEBC.DEBC_DATDELETED is null and DEBB.DEBB_DEBC_NO = DEBC.DEBC_NO and DEBB.DEBB_ACTINVAH >=0 and CMON.CMON_ADMNO = DEBC.DEBC_ADMNO and CMON.CMON_CLNTNO = DEBC.DEBC_CLNTNO and CMON.CMON_YEAR = :8 and CMON.CMON_MONTH = :9 and CMON.CMON_CURRENCY = ADMN.ADMN_CURRENCY and CMON.CMON_CURRBALDEBTORS > 0 and
DEBC.DEBC_DEBH in ( SELECT DEBH.DEBH_NO from :1/DEBH DEBH where DEBH.DEBH_HADR_COUNTRY=':10' and DEBH.DEBH_DATDELETED is null and DEBH.DEBH_CLNT_ADMNO=0 and DEBH.DEBH_CLNT_CLNTNO=0 and DEBH.DEBH_NO<>:2 and DEBH.DEBH_NO>2)
) order by INVO.INVO_DEBH_NO, INVO.INVO_INVOICENO for fetch only
